  <script>
    if ($("#VIEW_TYPE").length <= 0) {

      $("#form1").append(
        "<input type='hidden' name='VIEW_TYPE' value='FULL'>"
      );

    }
  </script>

  <script>
    var noRedirect = document.getElementById("NO_REDIRECT_ON_POOR")
    //append to form element that you want .
    var form = document.getElementsByTagName("form")[0];

    if (!noRedirect && form) {
      var input = document.createElement("input");

      input.setAttribute("type", "hidden");

      input.setAttribute("name", "NO_REDIRECT_ON_POOR");
      input.setAttribute("id", "NO_REDIRECT_ON_POOR");

      input.setAttribute("value", "false");

      form.appendChild(input);
    }



    var creditGrade = document.getElementsByName("CRED_GRADE")[0];


    function getformContainingCreditGrade(creditGrade) {
      var gradParentEl = creditGrade.parentElement;

      var shouldContinue = true;

      do {

        if (gradParentEl == null || gradParentEl.tagName.toString() == "BODY" ||
          gradParentEl.tagName.toString() == "FORM") {
          shouldContinue = false;
        } else {
          gradParentEl = gradParentEl.parentElement;
        }

      } while (shouldContinue)

      return gradParentEl;
    }

    function forceSubmitForm() {

      var formContainGrade = getformContainingCreditGrade(creditGrade);
      if (formContainGrade != null || formContainGrade.tagName.toString() == "FORM") {
        isSubmit = true; // in case of exit popup setup cause it not to show

        try {
          self.exitPop.setCookie("poor_redirect", true);
          self.exitPop.submitButtonClick();
        } catch (ev) {

        }

        formContainGrade.submit();
        //window.location.href = window.location.href + "&__submit=1&linkOutRedir=true" ;
      }
    }

    document.addEventListener("click", function () {
      var noRedirect = document.getElementById("NO_REDIRECT_ON_POOR")

      if (noRedirect && noRedirect.value == "true") {
        return;
      }

      if (creditGrade != null && creditGrade.value == "POOR") {
        var showPoorListing = document.getElementById("SHOW_POOR_CREDIT_OFFER")

        if (showPoorListing && showPoorListing.value == "true") {
          self.exitPop["ClicksNet"].showOfferWall("ClicksNet");
          return;
        }

        if (FS.slideshow !== undefined) {
          FS.slideshow.prototype.changeSlide = function () { };
        } else if (typeof slideManager != "undefined") {
          slideManager.changingSlide = function () { };
        }

        forceSubmitForm();
      }
    });
  </script>

  <script type="text/javascript">
        function setCookie(name, value, days) {
          var date = new Date();
          date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
          var expires = "; expires=" + date.toGMTString();
          document.cookie = name + "=" + value + expires + ";path=/";
        }

        function getParam(p) {
          var match = RegExp('[?&]' + p + '=([^&]*)').exec(window.location.search);
          return match && decodeURIComponent(match[1].replace(/\+/g, ' '));
        }

        // Add required hidden inputs to the form
        $("#form1").append("<input type='hidden' name='aid' value='41091'>");
        $("#form1").append("<input type='hidden' name='ip_address' value='{{ $ipAddress }}'>");
        $("#form1").append("<input type='hidden' name='capture_time' value='{{ $captureTime }}'>");

        var gclid = getParam('gclid');

        if (gclid) {
          var gclsrc = getParam('gclsrc');
          if (!gclsrc || gclsrc.indexOf('aw') !== -1) {
            setCookie('gclid', gclid, 90);
            addGCLIDToForm(gclid);
          }
        }

        function addGCLIDToForm(gclid) {
          try {
            if ($('#gclid_field').length <= 0) {
              $("#form1").append("<input type='hidden' id='GCLID' name='GCLID' value='" + gclid + "'>")
            }
            $('#GCLID').val(gclid);
          } catch (err) {
          }
        }
  </script>
